package forLoop;

import java.util.Iterator;

import zahidwork.forloopexample;

public class ForLoop1 {

	
	public void zahid() {
		
		for (int i = 15; i > 1; i--) {
		System.out.println(i);	
		}
		
		for (int i = 0; i < 10; i++) {
			System.out.println(i);	
		}
		for (int i = 0; i < 20; i++) {
			System.out.println(i);
		}
	}
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ForLoop1 d = new ForLoop1();
		d.zahid();
	

	}

}
